import React, { useContext, useRef } from 'react'
import './CSS/ShopCategory.css'
import { ShopContext } from '../Context/ShopContext'
import dropdown_icon from '../Components/Assets/dropdown_icon.png'
import Item from '../Components/Item/Item'

const ShopCategory = (props) => {
  const { all_products } = useContext(ShopContext)
  const sortRef = useRef();
  const dropdown_toggle = (e) => {
      sortRef.current.classList.toggle('sort-menu-visible');
      e.target.classList.toggle('open');
    }
  return (
    <div className='shop-category'>
      <img className='shopcategory-banner' src={props.banner} alt=""  />
      <div className="shopcategory-indexSort">
        <p>
          <span>Showing 1-12</span> out of 26 products
        </p>
        <div className="shopcategory-sort">
          Sort by <img src={dropdown_icon} alt="" onClick={dropdown_toggle} />
        </div>
        <div ref={sortRef} className="sort-dropdown">
          <div className="sort-type">Price</div>
          <div className="sort-type">Name (A-Z)</div>
          <div className="sort-type">Name (Z-A)</div>
        </div>
      </div>
      <div className="shopcategory-products">
        {all_products.map((item, i) => {
          if (props.category === item.category) {
            return <Item key={i}
              id={item.id}
              name={item.name}
              image={item.image}
              new_price={item.new_price}
              old_price={item.old_price} />
          }
          else{
            return null;
          }
        })}
      </div>
      <div className="shopcategory-loadmore">
        Explore More
      </div>
    </div>
  )
}

export default ShopCategory
